angular.module("app").directive("productbox",function(){return{restrict:"A",replace:!1,template:'<div class="product-box"><figure class="product-box-figure"><a href="#/produto/{{id}}"><img class="product-box-mediumimage" src="assets/images/products/{{image}}" alt="{{name}}"item-prop="image"></a><figcaption><a class="product-box-name" href="#/produto/{{id}}">{{name}}</a><a class="product-box-short-description" href="#/produto/{{id}}">{{desc}}</a></figcaption></figure><p class="product-box-fullprice" item-prop="price">{{price | currency}}</p></div>',scope:{id:"=",name:"=",desc:"=",image:"=",price:"="}}});